) separator '、') czssScore, group_concat((case when d.subjectid in(30) then s.subjectname end ) separator '、') kjjc, group_concat((case when d.subjectid in(30) then d.score end ) separator '、') kjjcScore, group_concat((case when d.subjectid in(31) then s.subjectname end ) separator '、') cjfg, group_concat((case when d.subjectid in(31) then d.score end ) separator '、') cjfgScore, group_concat((case when d.subjectid in(32) then s.subjectname end ) separator '、') cjkj, group_concat((case when d.subjectid in(32) then d.score end ) separator '、') cjkjScore, group_concat((case when d.subjectid in(33) then s.subjectname end ) separator '、') jjfjc, group_concat((case when d.subjectid in(33) then d.score end ) separator '、') jjfjcScore, group_concat((case when d.subjectid in(34) then s.subjectname end ) separator '、') qhjczs, group_concat((case when d.subjectid in(34) then d.score end ) separator '、') qhjczsScore, group_concat((case when d.subjectid in(35) then s.subjectname end ) separator '、') qhflfg, group_concat((case when d.subjectid in(35) then d.score end ) separator '、') qhflfgScore, group_concat((case when d.subjectid in(36) then s.subjectname end ) separator '、') zqsc, group_concat((case when d.subjectid in(36) then d.score end ) separator '、') zqscScore, group_concat((case when d.subjectid in(37) then s.subjectname end ) separator '、') jrsc, group_concat((case when d.subjectid in(37) then d.score end ) separator '、') jrscScore from hj_dealrecord d left join hj_subject s on s.subjectid = d.subjectid where 1=1 and d.dr = 0
