mapper.select:
select s.*,u.username belongName, d.dealrecordId,d.subjectid, c.courseName courseName,s.dealprice,s.dealtime,
group_concat(case when d.subjectid in (1,2) or dd.subjectid in (1,2) then (case when d.ispass is null then dd.ispass else d.ispass end ) end) as afp, group_concat(case when d.subjectid in (3,8) or dd.subjectid in (3,8) then(case when d.ispass is null then dd.ispass else d.ispass end )end) as touzi, group_concat(case when d.subjectid in (4,9) or dd.subjectid in (4,9) then (case when d.ispass is null then dd.ispass else d.ispass end) end) as baoxian, group_concat(case when d.subjectid in (5,10) or dd.subjectid in (5,10) then (case when d.ispass is null then dd.ispass else d.ispass end)end) as shuiwu, group_concat(case when d.subjectid in (6,11)or dd.subjectid in (6,11) then (case when d.ispass is null then dd.ispass else d.ispass end) end) as fuli, group_concat(case when d.subjectid in (7,12)or dd.subjectid in (7,12) then (case when d.ispass is null then dd.ispass else d.ispass end)end) as zonghe, group_concat(case when d.subjectid in (31) or dd.subjectid in (31) then (case when d.ispass is null then dd.ispass else d.ispass end) end) as cejing, group_concat(case when d.subjectid in (30) or dd.subjectid in (30) then (case when d.ispass is null then dd.ispass else d.ispass end) end) as kuaiji,
group_concat((select sb.subjectname from hj_subject sb where sb.subjectid = d.subjectid or sb.subjectid = dd.subjectid) separator '、') subjectname from hj_student s left join hj_dealrecord d on d.resourceId = s.resourceId left join hj_dealrecord dd on dd.studentId = s.studentId left join hj_course c on c.courseid = s.courseid left join hj_user u on u.userid = s.belongid where 1=1 and s.dr = 0 and s.studentstate in (3,4,5,6,7)
and s.phone=#{phone}
and s.studentId in(${studentIds})
and s.arrive_time >= #{arriveStartTime}
and s.arrive_time <= #{arriveEndTime}
and s.remitWay like concat('%','${remitWay}','%')
and s.remituser like concat('%','${remituser}','%')
and s.studentName like concat('%','${studentName}','%')
and s.idCard=#{idCard}
and s.belongid=#{belongid}
and s.courseid=#{courseid}
group by s.studentId ,s.resourceId order by s.commit_time desc

select s.*,d.deptname deptName,u.deptgroup deptgroupName,u.username belongName,u.state userstate from hj_student s left join hj_user u on u.userid = s.belongid left join hj_dept d on d.deptid = u.deptid where 1=1 and s.dr = 0 and s.isolddata = 0 AND u.userid!=97 and u.userid!=98
and s.belongid = #{userid}
and s.dealTime >= #{dealStartTime}
and s.dealTime <= #{dealEndTime}
and s.studentstate in(3,4,5,6,7) group by s.studentId order by u.deptgroup ,u.userid,s.courseid

