mapper.select:
select uu.username customerName, s.*,u.username belongName,(s.dealprice + s.netedumoney) sumPayMoney from hj_student s left join hj_user u on u.userid = s.belongid left join hj_user uu on uu.userid = s.customerId where 1=1 and s.dr = 0
and s.studentId=#{studentId}
and s.belongid=#{belongid}
and s.studentstate = 2
and studentId in(${studentIds})

select d.dealrecordId,d.resourceId,d.studentId,d.courseid,d.subjectid, c.courseName courseName, group_concat((select sb.subjectname from hj_subject sb where sb.subjectid = d.subjectid) separator '、') subjectname from hj_dealrecord d left join hj_course c on c.courseid = d.courseid where 1=1
and d.studentId = #{studentId}
and d.resourceId = #{resourceId}
group by d.courseid

select d.*,sb.subjectname subjectname, c.courseName courseName from hj_dealrecord d left join hj_course c on c.courseid = d.courseid left join hj_subject sb on sb.subjectid = d.subjectid where 1=1
and d.studentId = #{studentId}
and d.resourceId = #{resourceId}
group by d.subjectid

